package forconditionProgram;

public class Weekdayprogram {
	void weekdays() {
		for(int i=5;i<=8;i++) {
			System.out.println("Weekdays1"+i);
			for(int j=5;j<=7;j++) {
				System.out.println("Days"+j);
			}
		}
		
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Weekdayprogram obj=new Weekdayprogram();
		obj.weekdays();

	}

}
