package testingprograms;

public class TestingPrograms {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	int	No=17;
	int	i =2 ;
	boolean Prime =true;
	while(i<No) {
	if(No%i==-0) {
		System.out.print("Not Prime");
		Prime=false;
		i=i+1;
		
	}}
		
		if(Prime==true) {
	System.out.print("Prime");
	}}}
	


